mapper.select:
  	select
  	
  	from leave_message
  	
		
			leavemsgtime >= #{startTime}
		
		
			and leavemsgtime <= #{endTime}
		
		
			and orderno = #{orderno}
		
		
		 	and replaystatu = #{relyStatus}
		
		
			and useremail = #{userEmail}
		
		
			and blanklist = #{blacklist}
		
		
			and tid = #{leaveMessageType}
		
  	
  	order by
  		leavemsgtime ${orderKey}
  	limit
  		#{start},#{pageSize}

  	select
  		count(msgid)
  	from leave_message
  	
		
			leavemsgtime >= #{startTime}
		
		
			and leavemsgtime <= #{endTime}
		
		
			and orderno = #{orderno}
		
		
		 	and replaystatu = #{relyStatus}
		
		
			and useremail = #{userEmail}
		
		
			and blanklist = #{blacklist}
		
		
			and tid = #{leaveMessageType}
